update.sql:
alter table WorkspaceMaterialReply add state varchar(255) not null;
alter table WorkspaceMaterialReply add submitted datetime;
alter table WorkspaceMaterialReply add withdrawn datetime;
update WorkspaceMaterialReply set state = 'SUBMITTED', submitted = lastModified; 
update WorkspaceMaterialReply r, WorkspaceMaterialEvaluation e set state = 'EVALUATED' where r.workspaceMaterial_id = e.workspaceMaterialId and r.userEntityId = e.studentEntityId;
